import React from "react";
import { Alert, Row, Col, Image, Badge } from "react-bootstrap";
import { Link } from "react-router-dom";

const SingleDish = (props) => {
      if (props.dish) {
        return (
    <Row>
      <Col md={8} className="ml-auto mr-auto">
        <Row>
          <Col md={4}>
            <Link to={"/details/" + props.dish}>
              <Image
                src={props.dish.image}
                className="my-2 w-100 h-auto"
              />
            </Link>
          </Col>
          <Col md={8} className="mt-auto mb-auto">
            <h4>
              {props.dish.name}
              <Badge variant="warning" className="ml-2">{props.dish.price}</Badge>
              <Badge variant="danger">{props.dish.label}</Badge>
            </h4>
            {props.dish.description}
          </Col>
        </Row>
      </Col>
    </Row>
)
        } else {
          return <Alert variant="danger">There is not dish like this!</Alert>
        }
      };
export default SingleDish;
